import React from 'react';
import { Image, StyleSheet, Text, View, ImageBackground, Button, TouchableOpacity } from 'react-native';
import { NavigationContainer } from '@react-navigation/native';
import { createNativeStackNavigator } from '@react-navigation/native-stack';
import Data from '../../Constantes/dataBateaux.json';
import BateauDesc from './BateauDesc';
import Icon from 'react-native-vector-icons/FontAwesome';


var bateauxList = Data;

export default class BateauHome extends React.Component{
  constructor(props){
    super(props)
  }
  render(){
    return (

        <ImageBackground source={require('../../App_Resources/iOS/background.png')} style={{ width: "100%", height: "100%" }} resizeMode="cover" >
        	<View style={styles.simpleFlex}>
	          	<Text style={styles.title}>Nos bateaux partenaires</Text>

		        <Text> Tous les eaux mènent à Thibault.</Text>
		        <Text>     06.63.99.99.78 </Text>
		        <Text>   lebateaudethibault@gmail.com</Text>
		        <Text>www.facebook.com/lebateaudethibault</Text>
		    </View>
		    <View style={styles.btnBlock}>
		        {bateauxList.map((value, index)=>{
		          return <TouchableOpacity 
		          	style={styles.btn}
		            key={index}
		            onPress = {()=>{
		              this.props.navigation.navigate('BateauDesc', {id:value.id});
		            }}>
		            <View style={styles.btnFlex}>
			            <Image source={require('../../App_Resources/iOS/'+value.imageHome)}
			          style={styles.photo}/>
			          	<Text style={styles.btnText}>{value.name}</Text>
		          	</View>
		          </TouchableOpacity>
		        })}

		        <TouchableOpacity style={styles.btn}>
		        	<View style={styles.btnFlex}>
			          <Image source={require('../../App_Resources/iOS/ancre.png')} style={styles.photo}/>
			          <Text style={styles.btnText}>Contact</Text>
			        </View>
		        </TouchableOpacity>
		        <TouchableOpacity style={styles.btn}>
		          	<View style={styles.btnFlex}>
			          <Image source={require('../../App_Resources/iOS/ancre.png')} style={styles.photo}/>
			          <Text style={styles.btnText}>Contact</Text>
			        </View>
		        </TouchableOpacity>
	        </View>
        </ImageBackground>
    )
  }
}


const styles = StyleSheet.create({
  background:{
    width: "100%",
    height: "100%"
  },
  btnBlock:{
  	flex: 1,
  	flexDirection:"row",
  	flexWrap: 'wrap',
  	marginLeft: '2%',
  	marginRight: '2%'
  },
  photo:{
    width:'31%',
    height: '45%',
    borderColor:'50%',
    marginLeft:'10%',
    marginTop:'20%',
    marginBottom:'30%'
  },
  btn:{
    width: '50%',
    height: '33%',
    backgroundColor: "grey",
  },
  btnText:{
    marginTop: '30%',
    fontSize:'100%',
    marginLeft:'5%',
    color: "white",
    textAlign:'center',
 },
 btnFlex:{
 	flex:2,
 	flexDirection:"row"
 },
 simpleFlex:{
 	flex:1
 },
 title:{
 	fontSize: '150%',
 	textAlign: "center",
 	marginTop: '10%',
 	color: 'white'
 },

})
