import React from 'react';
import { Image, StyleSheet, Text, View, ImageBackground, Button, TouchableOpacity } from 'react-native';
import { NavigationContainer } from '@react-navigation/native';
import { createNativeStackNavigator } from '@react-navigation/native-stack';
import Data from '../../Constantes/dataBateaux.json';
import BateauDesc from './BateauDesc';
import Icon from 'react-native-vector-icons/FontAwesome';


var bateauxList = Data;

export default class BateauHome extends React.Component{
  constructor(props){
    super(props)
  }
  render(){
    return (

        <ImageBackground source={require('../../App_Resources/iOS/background.png')} style={styles.background} resizeMode="cover" >
        	<View style={{ flex: 1}}>
	          	<Text>Nos bateaux partenaires</Text>

		        <Text> Tous les eaux mènent à Thibault.</Text>
		        <Text>     06.63.99.99.78 </Text>
		        <Text>   lebateaudethibault@gmail.com</Text>
		        <Text>www.facebook.com/lebateaudethibault</Text>
		    </View>
		    <View style={{ flex: 1, flexDirection:"row", flexWrap: 'wrap', marginLeft: '2%',
  marginRight: '2%'}}>
		        {bateauxList.map((value, index)=>{
		          return <TouchableOpacity 
		          	style={styles.btn}
		            key={index}
		            onPress = {()=>{
		              this.props.navigation.navigate('BateauDesc', {id:value.id});
		            }}>
		            <Image source={require('../../App_Resources/iOS/'+value.imageHome)}
		          style={styles.photo}/>
		          <Text style={styles.text}>{value.name}</Text>
		          </TouchableOpacity>
		        })}

		        <TouchableOpacity style={styles.btn}>
		          <Image source={require('../../App_Resources/iOS/ancre.png')}/>
		          <Text style={styles.text}> Contact </Text>
		        </TouchableOpacity>
		        <TouchableOpacity style={styles.btn}>
		          <Image source={require('../../App_Resources/iOS/ancre.png')}/>
		          <Text style={styles.text}> Contact </Text>
		        </TouchableOpacity>
	        </View>
        </ImageBackground>
    )
  }
}




const styles = StyleSheet.create({
    entier:{
        flex:1,
        flexDirection:'row',
        justifyContent:'flex-start'
    },
    header:{
        flex:0.2,
        flexDirection:'row',
        justifyContent:'flex-start',
        backgroundColor: 'black',
        /*opacity: 0.4*/
    },
    headerLeft:{
        flex:1,
    },
    headerMiddle:{
       flex:10,
    },
    headerRight:{
       flex:1,
    },
    headerImg:{
        widht:"100%"/*20*/,
        height:"100%",
        /*backgroundColor:'red',*/
        resizeMode:'contain'
    },
    headerImg1:{
        widht:20,
        height:20,
        backgroundColor:'green',
        resizeMode:'contain'
    },
    block:{
    flex:1,
    flexDirection:'column',
    justifyContent : 'space-between',
    /*padding: "2%",*/

  },
  combImgText:{
    /*width:70,
    height:60,*/
    flexDirection:'row',
    backgroundColor: 'gray',
    borderRadius:1,
    flex:0.9
    /*position: 'relative',
    zIndex:2*/
  },
  text:{
     /*width: "100%", 
     height: "100%",*/
     textAlign:'center',
     paddingTop: 1,
     fontSize:25,
     alignSelf:'center'
     /*paddingRight:10,*/
     /*backgroundColor:"red"*/
  },
  image:{
    width:60,
    height: 60,
    borderColor:'50%',
    marginLeft:10,
    marginTop:30,
    marginBottom:10
  },
  background: {
    width: "100%", height: "100%" ,
    flex: 1
  }
})
