import React from 'react'
import { Image,StyleSheet, Text, View } from 'react-native';
import restaudata from '../../Constantes/Ship.json';
const restau = restaudata[0];

const  ImageRestau = (props) => {
  const getImage = props.restau.image;
  console.log(getImage);


  return (
    <View style={styles.combine_components}>
      <Text>{restau.name}</Text>
      <Image source={getImage}
//            <Image source={require(props.image)}
        style={styles.photo}/>
              <Text>{props.restau.description}</Text>
    </View>
  )
}

export default ImageRestau

const styles = StyleSheet.create({
  combine_components:{
    marginTop:'40%',
    width: '100%',
    height: '50%',
    color:'blue',
    display: 'flex',
    flexDirection:'row'
  },
  photo:{
    width: '70%',
    height: '300%',
    borderColor:'50%'
  },
  btn:{
    width: '60%',
    height: '100%',
    borderColor:'50%'
  },
})
