import React from 'react';
import { StyleSheet,TouchableOpacity,Image,Text, View } from 'react-native';
import { NavigationContainer, useNavigationContainerRef } from '@react-navigation/native';

//import { ContextAccueil} from './ContextAccueil'
const Recette = () => {
    //const styles=useContext(ContextAccueil)
    const navigationRef = useNavigationContainerRef();
    return (
        <View /*style={styles.padd}*/>
        <View style={styles.block}>
      {/* Add the text inside to describe his entreprise */}

            <TouchableOpacity style={styles.combImgText}
            onPress = {()=>{
              navigationRef.navigate('Recettes');
            }}>
                <Image style={styles.image}
                source={require('../../App_Resources/iOS/recette.png')} />
                <Text style={styles.text}> Recettes</Text>
            </TouchableOpacity>
        </View>

    {/* It will be use in
    <Image source={require('../../App_Resources/iOS/background.png')}
     style={styles.imageBack}/>*/}
    </View>
    )
}

export default Recette

const styles = StyleSheet.create({
    block:{
    flex:1,
    flexDirection:'column',
    justifyContent : 'flex-start',
    padding: "2%",
  },
  combImgText:{
    /*width:70,
    height:60,*/
    flexDirection:'row',
    backgroundColor: 'blue',
    borderRadius:1,
    /*position: 'relative',
    zIndex:2*/
  },
  text:{
     /*width: "100%",
     height: "100%",*/
     textAlign:'center',
     paddingTop: 10,
     fontSize:20
     /*backgroundColor:"red"*/
  },
  image:{
    width:50,
    height: 50,
    borderColor:'50%',
  },
})
