import React from 'react'
import { Image,StyleSheet, Text, View } from 'react-native';

const  ImageRestau = (props) => {
  const getImage = props.restau.image;
  console.log(getImage);

  {props.map((section, i) => (
    <CategoryCard
      key={i}
      source={section.image}
      title={section.title}
    />
 ))}


  return (
    <View style={styles.combine_components}>
      <Text>{props.restau.name}</Text>
      <Image source={props.restau.image}
//            <Image source={require(props.image)}
        style={styles.photo}/>
              <Text>{props.restau.description}</Text>
    </View>
  )
}

export default ImageRestau

const styles = StyleSheet.create({
  combine_components:{
    marginTop:'40%',
    width: '100%',
    height: '50%',
    color:'blue',
    display: 'flex',
    flexDirection:'row'
  },
  photo:{
    width: '40%',
    height: '100%',
    borderColor:'50%'
  },
  btn:{
    width: '60%',
    height: '100%',
    borderColor:'50%'
  },
})
